(2, 1, 101, 'course-001', 'Web Development', '49.99', '<div class=\"course-description\">\n  <h3 style=\"font-weight: bold; color: #000;\">What This Course Offers</h3>\n  <p>\n    Dive into a structured learning path designed to transform your skillset from beginner to pro. Our course blends\n    theory with hands-on examples and projects.\n  </p>\n\n  <h4>What You’ll Learn?</h4>\n  <ul style=\"list-style-type: \'✔️ \'; padding-left: 1.2em;\">\n    <li>Master the MERN stack from scratch</li>\n    <li>Develop complex apps using React and Node.js</li>\n    <li>Implement real-time features using sockets</li>\n    <li>Understand backend API structuring and deployment</li>\n  </ul>\n\n  <p>\n    We ensure every learner can walk away with job-ready confidence and a strong portfolio.\n  </p>\n</div>\n', '4 weeks', 'English', 'yes', '4.5', 20, 300, 'yes', 'https://img.freepik.com/free-photo/young-man-writing-notebook-study-session_23-2149285403.jpg', 'published', '2025-07-13 10:11:15', '2025-07-13 19:24:34', 'admin', 0),
(3, 2, 102, 'course-002', 'Advanced JavaScript', '59.99', '<div class=\"course-description\">\n  <h3 style=\"font-weight: bold; color: #000;\">Course Description</h3>\n  <p>\n    This course is your gateway to understanding full-stack development. You\'ll explore both front-end and back-end technologies in a project-driven environment.\n  </p>\n\n  <h4>What You’ll Learn?</h4>\n  <ul style=\"list-style-type: \'✔️ \'; padding-left: 1.2em;\">\n    <li>Create fast, dynamic web apps using JavaScript</li>\n    <li>Work with databases and REST APIs</li>\n    <li>Integrate frontend with backend seamlessly</li>\n    <li>Build and deploy on cloud platforms</li>\n  </ul>\n\n  <p>\n    The curriculum is designed by industry experts to align with modern development trends.\n  </p>\n</div>\n', '6 weeks', 'English', 'yes', '4.7', 30, 250, 'yes', 'https://img.freepik.com/free-photo/young-man-writing-notebook-study-session_23-2149285403.jpg', 'published', '2025-07-13 10:11:15', '2025-07-13 19:24:44', 'admin', 0),
(4, 3, 103, 'course-003', 'Python for Data Science', '69.99', '<div class=\"course-description\">\n  <h3 style=\"font-weight: bold; color: #000;\">Master the Fundamentals</h3>\n  <p>\n    Learn programming from scratch. No prior experience needed! This beginner-friendly course takes you step-by-step\n    through the core concepts.\n  </p>\n\n  <h4>What You’ll Learn?</h4>\n  <ul style=\"list-style-type: \'✔️ \'; padding-left: 1.2em;\">\n    <li>Variables, loops, and conditions in Python</li>\n    <li>Basic data structures and algorithms</li>\n    <li>Simple web scraping and automation</li>\n    <li>Handling files and user inputs</li>\n  </ul>\n\n  <p>\n    You’ll leave this course with a solid foundation to build on and confidence to take on more advanced topics.\n  </p>\n</div>\n', '8 weeks', 'English', 'yes', '4.8', 40, 500, 'no', 'https://img.freepik.com/free-photo/young-man-writing-notebook-study-session_23-2149285403.jpg', 'published', '2025-07-13 10:11:15', '2025-07-13 19:25:06', 'admin', 0),
(5, 4, 104, 'course-004', 'Graphic Design Basics', '39.99', '<div class=\"course-description\">\n  <h3 style=\"font-weight: bold; color: #000;\">Why Take This Course?</h3>\n  <p>\n    In today’s digital age, design and user experience define the success of products. This course empowers you to build\n    stunning UI/UX that stands out.\n  </p>\n\n  <h4>What You’ll Learn?</h4>\n  <ul style=\"list-style-type: \'✔️ \'; padding-left: 1.2em;\">\n    <li>Design thinking and prototyping</li>\n    <li>Wireframing with Figma</li>\n    <li>User testing strategies</li>\n    <li>Responsive, mobile-first design</li>\n  </ul>\n\n  <p>\n    By the end, you’ll have the skills to design apps users love.\n  </p>\n</div>\n', '5 weeks', 'English', 'yes', '4.4', 18, 180, 'no', 'https://img.freepik.com/free-photo/young-man-writing-notebook-study-session_23-2149285403.jpg', 'published', '2025-07-13 10:11:15', '2025-07-13 19:25:16', 'admin', 0),
(6, 5, 105, 'course-005', 'React from Zero to Hero', '79.99', '<div class=\"course-description\">\n  <h3 style=\"font-weight: bold; color: #000;\">Get Certified in Data Science</h3>\n  <p>\n    Join thousands of learners and gain the skills to work as a data analyst or data scientist. This course is packed with\n    real-world datasets and assignments.\n  </p>\n\n  <h4>What You’ll Learn?</h4>\n  <ul style=\"list-style-type: \'✔️ \'; padding-left: 1.2em;\">\n    <li>Data cleaning and visualization using Python</li>\n    <li>Data analysis with Pandas and NumPy</li>\n    <li>Basic machine learning workflows</li>\n    <li>Using Jupyter and Google Colab</li>\n  </ul>\n\n  <p>\n    Designed for both beginners and professionals wanting to switch to data.\n  </p>\n</div>\n', '7 weeks', 'English', 'yes', '4.9', 35, 420, 'yes', 'https://img.freepik.com/free-photo/young-man-writing-notebook-study-session_23-2149285403.jpg', 'published', '2025-07-13 10:11:15', '2025-07-13 19:25:26', 'admin', 0),
(7, 6, 106, 'course-006', 'UI/UX Design Principles', '44.99', '<div class=\"course-description\">\n  <h3 style=\"font-weight: bold; color: #000;\">Your First App: From Idea to Launch</h3>\n  <p>\n    Have an app idea? Learn how to plan, design, and deploy it without needing a full dev team. This course guides you\n    through each phase with tools used by pros.\n  </p>\n\n  <h4>What You’ll Learn?</h4>\n  <ul style=\"list-style-type: \'✔️ \'; padding-left: 1.2em;\">\n    <li>Conceptualizing and wireframing an app</li>\n    <li>Building UI with no-code tools</li>\n    <li>Connecting to APIs</li>\n    <li>Publishing on the app store</li>\n  </ul>\n\n  <p>\n    You’ll walk away with a complete project ready to showcase.\n  </p>\n</div>\n', '4 weeks', 'English', 'yes', '4.3', 22, 200, 'no', 'https://img.freepik.com/free-photo/young-man-writing-notebook-study-session_23-2149285403.jpg', 'published', '2025-07-13 10:11:15', '2025-07-13 19:25:34', 'admin', 0),
(8, 7, 107, 'course-007', 'Digital Marketing 101', '29.99', '<div class=\"course-description\">\n  <h3 style=\"font-weight: bold; color: #000;\">Full-Stack Project Bootcamp</h3>\n  <p>\n    Instead of endless tutorials, this course is 100% project-based. You\'ll build real apps while learning modern\n    full-stack technologies.\n  </p>\n\n  <h4>What You’ll Learn?</h4>\n  <ul style=\"list-style-type: \'✔️ \'; padding-left: 1.2em;\">\n    <li>React, Express, MongoDB stack</li>\n    <li>User authentication and JWT</li>\n    <li>CRUD operations with REST APIs</li>\n    <li>Image uploads and deployment</li>\n  </ul>\n\n  <p>\n    Ideal for those who learn best by doing.\n  </p>\n</div>\n', '3 weeks', 'English', 'yes', '4.2', 15, 150, 'no', 'https://img.freepik.com/free-photo/young-man-writing-notebook-study-session_23-2149285403.jpg', 'published', '2025-07-13 10:11:15', '2025-07-13 19:25:45', 'admin', 0),
(9, 8, 108, 'course-008', 'Node.js Crash Course', '54.99', '<div class=\"course-description\">\n  <h3 style=\"font-weight: bold; color: #000;\">Advanced JavaScript Concepts</h3>\n  <p>\n    Go beyond the basics and explore advanced features of JavaScript used in production applications.\n  </p>\n\n  <h4>What You’ll Learn?</h4>\n  <ul style=\"list-style-type: \'✔️ \'; padding-left: 1.2em;\">\n    <li>Closures and lexical scope</li>\n    <li>Prototypes and inheritance</li>\n    <li>Event loop and async patterns</li>\n    <li>Modular code with ES6+</li>\n  </ul>\n\n  <p>\n    Perfect for developers looking to level up their JS skills.\n  </p>\n</div>\n', '5 weeks', 'English', 'yes', '4.6', 25, 320, 'yes', 'https://img.freepik.com/free-photo/young-man-writing-notebook-study-session_23-2149285403.jpg', 'published', '2025-07-13 10:11:15', '2025-07-13 19:25:57', 'admin', 0),
(10, 9, 109, 'course-009', 'Machine Learning Intro', '89.99', 'Get started with ML & AI models.', '6 weeks', 'English', 'yes', '4.8', 28, 390, 'yes', 'https://img.freepik.com/free-photo/young-man-writing-notebook-study-session_23-2149285403.jpg', 'published', '2025-07-13 10:11:15', '2025-07-13 17:38:36', 'admin', 0),
(11, 10, 110, 'course-010', 'Freelancing & Remote Work', '19.99', '<div class=\"course-description\">\n  <h3 style=\"font-weight: bold; color: #000;\">Intro to AI & Machine Learning</h3>\n  <p>\n    This course demystifies the world of AI and gives you hands-on experience with basic machine learning techniques.\n  </p>\n\n  <h4>What You’ll Learn?</h4>\n  <ul style=\"list-style-type: \'✔️ \'; padding-left: 1.2em;\">\n    <li>What is Artificial Intelligence?</li>\n    <li>How ML models are trained</li>\n    <li>Linear regression and classification</li>\n    <li>Working with sklearn and pandas</li>\n  </ul>\n\n  <p>\n    No coding background required—just curiosity.\n  </p>\n</div>\n', '2 weeks', 'English', 'yes', '4.1', 10, 120, 'no', 'https://img.freepik.com/free-photo/young-man-writing-notebook-study-session_23-2149285403.jpg', 'published', '2025-07-13 10:11:15', '2025-07-13 19:26:16', 'admin', 0);

